mapper.select:
select

  distinct


from ques_type




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

  select qt.*,wo.name orgName
  from ques_type qt,windy_org wo
  where 1=1

  and qt.pid=#{pid}


  and qt.ins_id=${insId}


  and qt.ins_id in


		#{insId}  



  and qt.id in

  		#{quesTypeId}


  and qt.ins_id=wo.id
  order by creat_time desc
  limit #{startRow},#{pageSize}

  select count(*)
  from ques_type qt,windy_org wo
  where 1=1

  and qt.pid=#{pid}


  and qt.ins_id=${insId}



  and qt.ins_id in


		#{insId}  



  and qt.id in

  		#{quesTypeId}


  and qt.ins_id=wo.id

select qt.*,wo.name orgName
from ques_type qt,windy_org wo
where 1=1

and	qt.pid=#{pid}

and qt.ins_id=wo.id
 limit #{startRow},#{pageSize}

select count(*) from ques_type qt,windy_org wo where 1=1
and qt.pid=#{pid}
and qt.ins_id=wo.id

select vq.* from v_org_ques vq
 where 1=1 and qupid is not null 

 	and vq.opid=#{wid}
